mapper.select:
select * from o_order_sf

  and order_id = #{orderId}
  and id = #{id}
  and u_id = #{uId}
  and status = #{status}

order by status,created

select a.*,d.category_name,d.name,nullif(CONCAT('${httpUrl}',d.bright_pic),'${httpUrl}') as picture from o_order_sf a left join (select b.*,c.bright_pic from c_item_cat b ,c_item c where b.category_id = c.id) d on a.level_id = d.id
and a.u_id = #{uId} and a.id = #{id}
order by a.status,a.created desc
